import { Box } from "@mui/material";
import MyTypography from "../../../components/MyTypography";

const footer = () =>{
    return(
        <Box sx={{ padding: "64px 16px", backgroundColor: "#f1f1f1", width: "100%" }}>
            <Box sx={{
            display: 'flex',               // Usamos Flexbox
            alignItems: 'center',          // Alineamos los elementos verticalmente
            justifyContent: 'center',      // Alineamos los elementos horizontalmente
            width: '100%',                 // El contenedor ocupará el 100% del ancho
            }}>
                <i class="fa fa-facebook-official" aria-hidden="true"></i>

            <MyTypography 
                text="Powered by" 
                size="18px"
                letterSpacing="6px"
                fontFamily="Raleway"
                margin="10px 0"
                fontWeight="400"
                opacity="0.5"
            />
            {/* Aquí aplicamos el color al enlace */}
            <a href="https://www.w3schools.com/w3css/default.asp" style={{ marginLeft: '8px', color: '#262626' }}>
                W3.CSS
            </a>
            </Box>
        </Box>
    );
}

export default footer;
